import React, { useState } from 'react';
import { Line, Bar, Scatter } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const InferentialStatisticsVisualiser = ({ data, columnHeaders }) => {
  console.log(data)
  // console.log(col)
  const [selectedCategory, setSelectedCategory] = useState('hypothesis');
  const [selectedData, setSelectedData] = useState('');

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        type: 'category',
        labels: [],  // x-axis will dynamically take values from the selected column
      },
      y: {
        type: 'linear',
      },
    },
    plugins: {
      legend: {
        position: 'top',
      },
      tooltip: {
        mode: 'index',
        intersect: false,
      },
    },
  };

  // Helper function to map data for the dynamic chart based on selected column
  const getDataForChart = (columnName) => {
    return data.map((item) => item[columnName] !== undefined ? item[columnName] : 0);
  };

  // Update selected data when user selects a new column
  const handleColumnSelect = (e) => {
    setSelectedData(e.target.value);
  };

  // Chart data objects
  const chartData = {
    hypothesis: {
      zTestData: {
        labels: columnHeaders[0],  // Set x-axis labels to the selected column's data
        datasets: [
          {
            label: 'Z-Distribution',
            data: getDataForChart(selectedData), // Dynamically get data based on selected column
            borderColor: 'rgb(75, 192, 192)',
            tension: 0.4,
            fill: false,
          },
        ],
      },
      tTestData: {
        labels: getDataForChart(selectedData),  // Set x-axis labels to the selected column's data
        datasets: [
          {
            label: 'Sample Distribution',
            data: getDataForChart(selectedData), // Dynamically get data based on selected column
            backgroundColor: 'rgba(75, 192, 192, 0.5)',
          },
        ],
      },
    },
    confidence: {
      confidenceData: {
        labels: getDataForChart(selectedData),  // Set x-axis labels to the selected column's data
        datasets: [
          {
            label: 'Mean',
            data: getDataForChart(selectedData), // Dynamically get data based on selected column
            borderColor: 'rgb(75, 192, 192)',
            backgroundColor: 'rgba(75, 192, 192, 0.5)',
            type: 'line',
          },
        ],
      },
    },
    sampling: {
      randomSamplingData: {
        labels: getDataForChart(selectedData),  // Set x-axis labels to the selected column's data
        datasets: [
          {
            label: 'Random Samples',
            data: getDataForChart(selectedData), // Dynamically get data based on selected column
            backgroundColor: 'rgb(75, 192, 192)',
          },
        ],
      },
      stratifiedData: {
        labels: getDataForChart(selectedData),  // Set x-axis labels to the selected column's data
        datasets: [
          {
            label: 'Stratum Size',
            data: getDataForChart(selectedData), // Dynamically get data based on selected column
            backgroundColor: 'rgba(75, 192, 192, 0.5)',
          },
          {
            label: 'Sample Size',
            data: getDataForChart(selectedData), // Dynamically get data based on selected column
            backgroundColor: 'rgba(153, 102, 255, 0.5)',
          },
        ],
      },
    },
  };

  const renderHypothesisTests = () => (
    <div className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">Z-Test Distribution</h2>
        <div style={{ height: '300px' }}>
          <Line data={chartData.hypothesis.zTestData} options={options} />
        </div>
      </div>

      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">T-Test Comparison</h2>
        <div style={{ height: '300px' }}>
          <Bar data={chartData.hypothesis.tTestData} options={options} />
        </div>
      </div>
    </div>
  );

  const renderConfidenceIntervals = () => (
    <div className="bg-white p-6 rounded-lg shadow">
      <h2 className="text-xl font-bold mb-4">Confidence Intervals</h2>
      <div style={{ height: '300px' }}>
        <Line data={chartData.confidence.confidenceData} options={options} />
      </div>
    </div>
  );

  const renderSampling = () => (
    <div className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">Random Sampling Distribution</h2>
        <div style={{ height: '300px' }}>
          <Scatter data={chartData.sampling.randomSamplingData} options={options} />
        </div>
      </div>

      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">Stratified Sampling</h2>
        <div style={{ height: '300px' }}>
          <Bar data={chartData.sampling.stratifiedData} options={options} />
        </div>
      </div>
    </div>
  );

  return (
    <div className="max-w-4xl mx-auto p-6 space-y-6">
      <div className="text-center space-y-4">
        <h1 className="text-2xl font-bold">Inferential Statistics Visualization</h1>
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
          className="px-4 py-2 border rounded-md"
        >
          <option value="hypothesis">Hypothesis Testing</option>
          <option value="confidence">Confidence Intervals</option>
          <option value="sampling">Sampling Methods</option>
        </select>

        {/* Dropdown for selecting the column dynamically */}
        <select
          value={selectedData}
          onChange={handleColumnSelect}
          className="px-4 py-2 border rounded-md mt-4"
        >
          <option value="">Select Column</option>
          {columnHeaders && columnHeaders.map((header, index) => (
            <option key={index} value={header}>{header}</option>
          ))}
        </select>
      </div>

      {selectedCategory === 'hypothesis' && renderHypothesisTests()}
      {selectedCategory === 'confidence' && renderConfidenceIntervals()}
      {selectedCategory === 'sampling' && renderSampling()}
    </div>
  );
};

export default InferentialStatisticsVisualiser;
