        y = 2.0 - x;
        flg = 0;
      }
      // 1.2316 <= x < 1.7316
      else if (x >= YMIN - 0.27) {
        y = x - TC;
        flg = 1;
      }
      // 0.9 < x < 1.2316
      else {
        y = x - 1.0;
        flg = 2;
      }
