        p2 = VC + y * polyvalV(y);
        r += -0.5 * y + p1 / p2;
        break;
    }
  }
  // 2 <= x < 8
  else if (x < 8.0) {
    flg = trunc(x);
    y = x - flg;
    p = y * (SC + y * polyvalS(y));
    q = RC + y * polyvalR(y);
    r = 0.5 * y + p / q;
    z = 1.0; // gammaln(1+s) = ln(s) + gammaln(s)
