      // eslint-disable-line default-case
      case 7:
        z *= y + 6.0;

      /* Falls through */
      case 6:
        z *= y + 5.0;

      /* Falls through */
      case 5:
        z *= y + 4.0;

      /* Falls through */
      case 4:
        z *= y + 3.0;

      /* Falls through */
      case 3:
        z *= y + 2.0;
        r += ln(z);
