    t = ln(x);
    z = 1.0 / x;
    y = z * z;
    w = WC + z * polyvalW(y);
    r = (x - 0.5) * (t - 1.0) + w;
  }
  // 2**58 <= x <= Inf
  else {
    r = x * (ln(x) - 1.0);
  }
  if (isNegative) {
    r = nadj - r;
  }
  return r;
}

// EXPORTS //

module.exports = gammaln;

