import React, { useState } from 'react';
import { Line, Bar, Scatter } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const InferentialStatisticsVisualiser = ({ data, columnHeaders }) => {
  console.log(data);
  const [selectedCategory, setSelectedCategory] = useState('hypothesis');

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        type: 'category',
      },
      y: {
        type: 'linear',
      },
    },
    plugins: {
      legend: {
        position: 'top',
      },
      tooltip: {
        mode: 'index',
        intersect: false,
      },
    },
  };

  // Z-Test Chart Data
  const zTestData = {
    labels: columnHeaders || ['No Data'],
    datasets: [
      {
        label: 'Z-Distribution',
        data: data.map((item) => item[5]),
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.4,
        fill: false,
      },
    ],
  };

  // T-Test Chart Data
  const tTestData = {
    labels: columnHeaders || ['No Data'],
    datasets: [
      {
        label: 'Sample Distribution',
        data: data.map((item) => item[2]),
        backgroundColor: 'rgba(75, 192, 192, 0.5)',
      },
    ],
  };

  // Chi-Square Chart Data
  const chiSquareData = {
    labels: columnHeaders || ['No Data'],
    datasets: [
      {
        label: 'Observed',
        data: data.map((item) => item[3]),
        backgroundColor: 'rgba(75, 192, 192, 0.5)',
      },
      {
        label: 'Expected',
        data: data.map((item) => item[4]),
        backgroundColor: 'rgba(153, 102, 255, 0.5)',
      },
    ],
  };

  // ANOVA Chart Data
  const anovaData = {
    labels: columnHeaders || ['No Data'],
    datasets: [
      {
        label: 'ANOVA',
        data: data.map((item) => item[9]),
        backgroundColor: 'rgba(255, 99, 132, 0.5)',
      },
    ],
  };

  // Confidence Intervals Chart Data
  const confidenceData = {
    labels: columnHeaders || ['No Data'],
    datasets: [
      {
        label: 'Mean',
        data: data.map((item) => item[5]),
        borderColor: 'rgb(75, 192, 192)',
        backgroundColor: 'rgba(75, 192, 192, 0.5)',
        type: 'line',
      },
      {
        label: 'Confidence Interval',
        data: data.map((item) => item[5] - 10),
        backgroundColor: 'rgba(75, 192, 192, 0.1)',
        borderColor: 'rgba(75, 192, 192, 0.1)',
        fill: '+1',
        type: 'line',
      },
      {
        label: 'Upper Bound',
        data: data.map((item) => item[5] + 10),
        backgroundColor: 'rgba(75, 192, 192, 0.1)',
        borderColor: 'rgba(75, 192, 192, 0.1)',
        fill: false,
        type: 'line',
      },
    ],
  };

  // Random Sampling Chart Data
  const randomSamplingData = {
    datasets: [
      {
        label: 'Random Samples',
        data: data.map((item) => ({
          x: item[0],
          y: item[6] !== undefined ? item[6] : 0,
        })),
        backgroundColor: 'rgb(75, 192, 192)',
      },
    ],
  };

  // Stratified Sampling Chart Data
  const stratifiedData = {
    labels: columnHeaders || ['No Data'],
    datasets: [
      {
        label: 'Stratum Size',
        data: data.map((item) => (item[7] !== undefined ? item[7] : 0)),
        backgroundColor: 'rgba(75, 192, 192, 0.5)',
      },
      {
        label: 'Sample Size',
        data: data.map((item) => (item[8] !== undefined ? item[8] : 0)),
        backgroundColor: 'rgba(153, 102, 255, 0.5)',
      },
    ],
  };

  // Cluster Sampling Chart Data
  const clusterSamplingData = {
    datasets: [
      {
        label: 'Cluster Samples',
        data: data.map((item) => ({
          x: item[0],
          y: item[10] !== undefined ? item[10] : 0,
        })),
        backgroundColor: 'rgb(255, 159, 64)',
      },
    ],
  };

  const renderHypothesisTests = () => (
    <div className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">Z-Test Distribution</h2>
        <div style={{ height: '300px' }}>
          <Line data={zTestData} options={options} />
        </div>
      </div>

      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">T-Test Comparison</h2>
        <div style={{ height: '300px' }}>
          <Bar data={tTestData} options={options} />
        </div>
      </div>

      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">Chi-Square Test</h2>
        <div style={{ height: '300px' }}>
          <Bar data={chiSquareData} options={options} />
        </div>
      </div>

      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">ANOVA</h2>
        <div style={{ height: '300px' }}>
          <Bar data={anovaData} options={options} />
        </div>
      </div>
    </div>
  );

  const renderConfidenceIntervals = () => (
    <div className="bg-white p-6 rounded-lg shadow">
      <h2 className="text-xl font-bold mb-4">Confidence Intervals</h2>
      <div style={{ height: '300px' }}>
        <Line data={confidenceData} options={options} />
      </div>
    </div>
  );

  const renderSampling = () => (
    <div className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">Random Sampling Distribution</h2>
        <div style={{ height: '300px' }}>
          <Scatter data={randomSamplingData} options={options} />
        </div>
      </div>

      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">Stratified Sampling</h2>
        <div style={{ height: '300px' }}>
          <Bar data={stratifiedData} options={options} />
        </div>
      </div>

      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">Cluster Sampling</h2>
        <div style={{ height: '300px' }}>
          <Scatter data={clusterSamplingData} options={options} />
        </div>
      </div>
    </div>
  );

  return (
    <div className="max-w-4xl mx-auto p-6 space-y-6">
      <div className="text-center space-y-4">
        <h1 className="text-2xl font-bold">Inferential Statistics Visualization</h1>
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
          className="px-4 py-2 border rounded-md"
        >
          <option value="hypothesis">Hypothesis Testing</option>
          <option value="confidence">Confidence Intervals</option>
          <option value="sampling">Sampling Methods</option>
        </select>
      </div>

      {selectedCategory === 'hypothesis' && renderHypothesisTests()}
      {selectedCategory === 'confidence' && renderConfidenceIntervals()}
      {selectedCategory === 'sampling' && renderSampling()}
    </div>
  );
};

export default InferentialStatistics
