import React from "react";

const RawDataTable = ({ data, columnHeaders, setData }) => {
  // Helper function to check for duplicate rows
  const getDuplicateIndices = (data) => {
    const seen = new Set();
    const duplicates = new Set();

    data.forEach((row, index) => {
      const rowString = JSON.stringify(row);
      if (seen.has(rowString)) {
        duplicates.add(index);
      } else {
        seen.add(rowString);
      }
    });

    return duplicates;
  };

  const duplicateIndices = getDuplicateIndices(data);

  // Handle cell edits to update the data
  const handleCellEdit = (rowIndex, colIndex, value) => {
    const updatedData = [...data];
    updatedData[rowIndex][colIndex] = value;
    setData(updatedData); // Update the data state
  };

  return (
    <table className="striped responsive-table">
      <thead>
        <tr>
          {columnHeaders.map((header, colIndex) => (
            <th key={colIndex}>{header}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {data.map((row, rowIndex) => (
          <tr
            key={rowIndex}
            className={duplicateIndices.has(rowIndex) ? "duplicate-row" : ""}
          >
            {row.map((cell, colIndex) => (
              <td
                key={colIndex}
                contentEditable
                onBlur={(e) =>
                  handleCellEdit(rowIndex, colIndex, e.target.innerText)
                }
              >
                {cell}
              </td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default RawDataTable;
