import React, { useState } from 'react';
import { LineChart, BarChart, ScatterChart, Line, Bar, Scatter, XAxis, YAxis, Tooltip } from 'recharts';

const InferentialStatisticsViz = () => {
  const [selectedCategory, setSelectedCategory] = useState('hypothesis');

  // Sample data - in a real application, this would come from your statistical calculations
  const sampleData = {
    hypothesis: {
      zTest: [
        { x: -3, y: 0.004, label: 'Critical Region' },
        { x: -2, y: 0.054, label: 'Rejection Region' },
        { x: -1, y: 0.242, label: 'Acceptance Region' },
        { x: 0, y: 0.399, label: 'Peak' },
        { x: 1, y: 0.242, label: 'Acceptance Region' },
        { x: 2, y: 0.054, label: 'Rejection Region' },
        { x: 3, y: 0.004, label: 'Critical Region' }
      ],
      tTest: [
        { value: 2.8, count: 15, type: 'Sample 1' },
        { value: 3.2, count: 18, type: 'Sample 1' },
        { value: 3.5, count: 22, type: 'Sample 1' },
        { value: 2.9, count: 14, type: 'Sample 2' },
        { value: 3.1, count: 20, type: 'Sample 2' },
        { value: 3.4, count: 19, type: 'Sample 2' }
      ],
      chiSquare: [
        { category: 'A', observed: 45, expected: 40 },
        { category: 'B', observed: 38, expected: 40 },
        { category: 'C', observed: 42, expected: 40 },
        { category: 'D', observed: 35, expected: 40 }
      ]
    },
    confidence: [
      { sample: 1, mean: 75, lower: 72, upper: 78 },
      { sample: 2, mean: 78, lower: 75, upper: 81 },
      { sample: 3, mean: 73, lower: 70, upper: 76 }
    ],
    sampling: {
      random: Array.from({ length: 20 }, (_, i) => ({
        id: i + 1,
        value: Math.random() * 100
      })),
      stratified: [
        { stratum: 'A', size: 30, samples: 10 },
        { stratum: 'B', size: 45, samples: 15 },
        { stratum: 'C', size: 25, samples: 8 }
      ]
    }
  };

  const renderHypothesisTests = () => (
    <div className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">Z-Test Distribution</h2>
        <div className="h-64">
          <LineChart
            width={600}
            height={200}
            data={sampleData.hypothesis.zTest}
            margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
          >
            <XAxis dataKey="x" />
            <YAxis />
            <Tooltip />
            <Line type="monotone" dataKey="y" stroke="#8884d8" />
          </LineChart>
        </div>
      </div>

      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">T-Test Comparison</h2>
        <div className="h-64">
          <BarChart
            width={600}
            height={200}
            data={sampleData.hypothesis.tTest}
            margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
          >
            <XAxis dataKey="value" />
            <YAxis />
            <Tooltip />
            <Bar dataKey="count" fill="#82ca9d" />
          </BarChart>
        </div>
      </div>

      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">Chi-Square Test</h2>
        <div className="h-64">
          <BarChart
            width={600}
            height={200}
            data={sampleData.hypothesis.chiSquare}
            margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
          >
            <XAxis dataKey="category" />
            <YAxis />
            <Tooltip />
            <Bar dataKey="observed" fill="#8884d8" name="Observed" />
            <Bar dataKey="expected" fill="#82ca9d" name="Expected" />
          </BarChart>
        </div>
      </div>
    </div>
  );

  const renderConfidenceIntervals = () => (
    <div className="bg-white p-6 rounded-lg shadow">
      <h2 className="text-xl font-bold mb-4">Confidence Intervals</h2>
      <div className="h-64">
        <ScatterChart
          width={600}
          height={200}
          margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
        >
          <XAxis dataKey="sample" type="number" domain={[0.5, 3.5]} />
          <YAxis domain={['auto', 'auto']} />
          <Tooltip />
          <Scatter data={sampleData.confidence} fill="#8884d8">
            {sampleData.confidence.map((entry, index) => (
              <Line
                key={`line-${index}`}
                type="monotone"
                data={[
                  { sample: entry.sample, value: entry.lower },
                  { sample: entry.sample, value: entry.upper }
                ]}
                stroke="#82ca9d"
              />
            ))}
          </Scatter>
        </ScatterChart>
      </div>
    </div>
  );

  const renderSampling = () => (
    <div className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">Random Sampling Distribution</h2>
        <div className="h-64">
          <ScatterChart
            width={600}
            height={200}
            margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
          >
            <XAxis dataKey="id" />
            <YAxis dataKey="value" />
            <Tooltip />
            <Scatter data={sampleData.sampling.random} fill="#8884d8" />
          </ScatterChart>
        </div>
      </div>

      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">Stratified Sampling</h2>
        <div className="h-64">
          <BarChart
            width={600}
            height={200}
            data={sampleData.sampling.stratified}
            margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
          >
            <XAxis dataKey="stratum" />
            <YAxis />
            <Tooltip />
            <Bar dataKey="size" fill="#8884d8" name="Stratum Size" />
            <Bar dataKey="samples" fill="#82ca9d" name="Sample Size" />
          </BarChart>
        </div>
      </div>
    </div>
  );

  return (
    <div className="max-w-4xl mx-auto p-6 space-y-6">
      <div className="text-center space-y-4">
        <h1 className="text-2xl font-bold">Inferential Statistics Visualization</h1>
        <select 
          value={selectedCategory} 
          onChange={(e) => setSelectedCategory(e.target.value)}
          className="px-4 py-2 border rounded-md"
        >
          <option value="hypothesis">Hypothesis Testing</option>
          <option value="confidence">Confidence Intervals</option>
          <option value="sampling">Sampling Methods</option>
        </select>
      </div>

      {selectedCategory === 'hypothesis' && renderHypothesisTests()}
      {selectedCategory === 'confidence' && renderConfidenceIntervals()}
      {selectedCategory === 'sampling' && renderSampling()}
    </div>
  );
};

export default InferentialStatisticsViz;
