  if (x < 0.0) {
    y = -x;
  } else {
    y = x;
  }
  // Argument reduction...
  k = 1;

  // Check if argument reduction is needed and if we can just return a small value approximation requiring less computation but with equivalent accuracy...
  if (y < SQRT2M1) {
    // if |x| < sqrt(2)-1 => ~0.41422
