import React, { useState } from "react";
import * as ss from "simple-statistics";
import { Line } from "react-chartjs-2"; // Import Chart.js Line chart
import { KolmogorovSmirnov } from "jstat"; // For KS-Test
import "../styles/DistributionAnalysis.css"; // Import external CSS file
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from "chart.js";
import jStat from "jstat"; // Import jstat for Beta distribution

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const DistributionAnalysis = ({ data, columnHeaders }) => {
  const [selectedColumn, setSelectedColumn] = useState("");
  const [selectedDistributionType, setSelectedDistributionType] = useState("");
  const [selectedDistribution, setSelectedDistribution] = useState("");
  const [chartData, setChartData] = useState(null);

  const continuousDistributions = [
    "Normal", "Exponential", "Uniform", "Gamma", "Log-Normal", "Beta"
  ];
  const discreteDistributions = [
    "Binomial", "Poisson", "Geometric", "Bernoulli", "Negative Binomial", "Hypergeometric", "Multinomial"
  ];


  const calculateAccuracy = (observed, expected, type) => {
    if (type === "Discrete") {
      // Chi-Square Test
      const chiSquare = observed.reduce((sum, obs, i) => {
        const exp = expected[i] || 0; // Handle missing values
        return exp > 0 ? sum + Math.pow(obs - exp, 2) / exp : sum;
      }, 0);
      const pValue = 1 - jStat.chisquare.cdf(chiSquare, observed.length - 1);
      return pValue; // Higher p-value means better fit
    } else if (type === "Continuous") {
      // KS-Test
      const { pValue } = KolmogorovSmirnov(observed, expected);
      return pValue; // Higher p-value means better fit
    }
    return 0;
  };

  const handleColumnChange = (event) => {
    const column = event.target.value;
    setSelectedColumn(column);
    setChartData(null);
    setSelectedDistributionType("");
    setSelectedDistribution("");

    if (column) {
      const colIndex = columnHeaders.indexOf(column);
      const columnData = data
        .map((row) => parseFloat(row[colIndex]))
        .filter((val) => !isNaN(val));

      if (columnData.length === 0) return;
    }
  };

  const handleDistributionTypeChange = (event) => {
    setSelectedDistributionType(event.target.value);
    setSelectedDistribution("");
    setChartData(null);
    setAccuracy(null); // Reset accuracy when distribution type changes
  };

  const handleDistributionChange = (event) => {
    const distribution = event.target.value;
    setSelectedDistribution(distribution);
  
    if (distribution && selectedColumn) {
      const colIndex = columnHeaders.indexOf(selectedColumn);
      const columnData = data
        .map((row) => parseFloat(row[colIndex]))
        .filter((val) => !isNaN(val));
  
      if (columnData.length === 0) return;
  
      // Generate chart data for the selected distribution
      const labels = Array.from({ length: columnData.length }, (_, index) => index + 1);
      let distributionData = [];
  
      switch (distribution) {
        case "Normal":
          const mean = ss.mean(columnData);
          const stddev = ss.standardDeviation(columnData);
          distributionData = columnData.map((val) => ss.zScore(val, mean, stddev));
          break;
        case "Exponential":
          const lambda = 1 / ss.mean(columnData);
          distributionData = columnData.map((val) => lambda * Math.exp(-lambda * val));
          break;
        case "Uniform":
          const min = Math.min(...columnData);
          const max = Math.max(...columnData);
          distributionData = columnData.map(() => 1 / (max - min));
          break;
        case "Gamma":
          const alphaGamma = Math.pow(ss.mean(columnData), 2) / Math.pow(ss.standardDeviation(columnData), 2);
          const betaGamma = Math.pow(ss.standardDeviation(columnData), 2) / ss.mean(columnData);
          distributionData = columnData.map(() => jStat.gamma.sample(alphaGamma, betaGamma));
          break;
        case "Log-Normal":
          const logMean = Math.log(ss.mean(columnData));
          const logStddev = Math.log(ss.standardDeviation(columnData));
          distributionData = columnData.map(() => Math.exp(logMean + logStddev * Math.random()));
          break;
        case "Beta":
          const alphaBeta = ss.mean(columnData) * (ss.mean(columnData) * (1 - ss.mean(columnData)) / Math.pow(ss.standardDeviation(columnData), 2) - 1);
          const betaBeta = (1 - ss.mean(columnData)) * (ss.mean(columnData) * (1 - ss.mean(columnData)) / Math.pow(ss.standardDeviation(columnData), 2) - 1);
          distributionData = columnData.map(() => jStat.beta.sample(alphaBeta, betaBeta));
          break;
          case "Binomial":
  const n = Math.max(...columnData);  // Sample size - Using the maximum value from columnData (it can be dynamic)
  const p = 0.5;  // Probability of success
  distributionData = columnData.map((val) => jStat.binomial.pdf(val, n, p));  // Apply binomial distribution
  break;          

case "Poisson":
  const lambda1 = ss.mean(columnData);  // Average rate (mean) - Calculating mean dynamically from columnData
  distributionData = columnData.map((val) => jStat.poisson.pdf(val, lambda1));  // Apply Poisson distribution
  break;

case "Geometric":
  const pGeo = 0.5;  // Probability of success
  distributionData = columnData.map((val) => {
    // Geometric distribution PMF formula: (1 - p)^(k-1) * p
    if (val > 0) {
      return Math.pow(1 - pGeo, val - 1) * pGeo;
    } else {
      return 0;  // Return 0 for invalid values
    }
  });  // Apply Geometric distribution
  break;

case "Bernoulli":
  const pBernoulli = 0.5;  // Probability of success
  distributionData = columnData.map((val) => {
    // Manual calculation of PMF for Bernoulli: p^x * (1-p)^(1-x)
    return val === 1 ? pBernoulli : 1 - pBernoulli;
  });  // Apply Bernoulli distribution manually
  break;

  case "Negative Binomial":
  // Binomial Coefficient function
  function binomialCoefficient(n, k) {
    if (k > n) return 0;
    let coeff = 1;
    for (let i = 1; i <= k; i++) {
      coeff = coeff * (n - (k - i)) / i;
    }
    return coeff;
  }

  const r = 10;  // Number of successes (you can adjust this dynamically)
  const pNegBin = 0.5;  // Probability of success
  
  // Using the Negative Binomial distribution formula:
  distributionData = columnData.map((val) => {
    if (val >= r) {
      const binomCoeff = binomialCoefficient(val - 1, r - 1); // Binomial coefficient part
      const prob = binomCoeff * Math.pow(pNegBin, r) * Math.pow(1 - pNegBin, val - r);
      return prob;  // Applying the Negative Binomial PMF
    } else {
      return 0;  // If val is less than the required number of successes, return 0
    }
  });
  break;

  

  case "Hypergeometric":
  const M = columnData.length;  // Total population size - dynamically based on the dataset
  const N = columnData.filter(val => val === 1).length;  // Number of successes in the population (assuming '1' represents success)
  const n2 = Math.floor(M / 2);  // Number of draws - dynamically chosen based on dataset, here it's half the population
  distributionData = columnData.map((val) => {
    // Dynamic Hypergeometric distribution calculation using values derived from columnData
    return hypergeometricPDF(val, M, N, n2);  // Apply Hypergeometric distribution
  });
  break;

// Hypergeometric distribution function
function hypergeometricPDF(k, M, N, n2) {
  if (k < 0 || k > n2) return 0; // Return 0 if k is out of valid range

  // Binomial coefficient function
  const binomialCoefficient1 = (n, k) => {
    if (k > n) return 0;
    let res = 1;
    for (let i = 0; i < k; i++) {
      res *= (n - i) / (i + 1);
    }
    return res;
  };

  // Compute the three binomial coefficients for the Hypergeometric distribution
  const coeff1 = binomialCoefficient1(N, k);  // Ways to choose k successes from N successes
  const coeff2 = binomialCoefficient1(M - N, n2 - k);  // Ways to choose (n2 - k) failures from (M - N) failures
  const coeff3 = binomialCoefficient1(M, n2);  // Ways to choose n2 draws from M total population

  // Apply the Hypergeometric distribution formula
  return (coeff1 * coeff2) / coeff3;
}


