// Multinomial PMF function
function multinomialPMF(counts, probabilities, trials) {
  // Ensure that counts sum up to trials
  const total = counts.reduce((acc, count) => acc + count, 0);
  if (total !== trials) {
    console.error("Counts do not sum up to the number of trials.");
    return 0;
  }

  // Calculate the factorials of counts
  const factorial = (n) => (n === 0 || n === 1) ? 1 : n * factorial(n - 1);
  
  // Compute the multinomial coefficient
  const numerator = factorial(trials);  // n!
  let denominator = 1;
  for (let i = 0; i < counts.length; i++) {
    denominator *= factorial(counts[i]);  // x1!, x2!, ..., xk!
  }

  // Compute the probability for the outcome
  let probability = 1;
  for (let i = 0; i < counts.length; i++) {
    probability *= Math.pow(probabilities[i], counts[i]);
  }

  // Return the final PMF
  return (numerator / denominator) * probability;
}

                       
