import React, { useState } from "react";
import { Line, Bar, Scatter } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const InferentialStatisticsVisualiser = ({ data, columnHeaders }) => {
  const [selectedCategory, setSelectedCategory] = useState("hypothesis");
  const [selectedColumn, setSelectedColumn] = useState(
    columnHeaders ? columnHeaders[0] : null
  );

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: { type: "category", title: { display: true, text: "Categories" } },
      y: { type: "linear", title: { display: true, text: "Values" } },
    },
    plugins: {
      legend: { position: "top" },
      tooltip: { mode: "index", intersect: false },
    },
  };

  const getColumnIndex = (header) => columnHeaders?.indexOf(header) ?? -1;

  const selectedColumnIndex = getColumnIndex(selectedColumn);

  const chartData = {
    hypothesis: {
      zTest: {
        labels: columnHeaders || ["No Data"],
        datasets: [
          {
            label: "Z-Distribution",
            data:
              selectedColumnIndex >= 0
                ? data.map((item) => item[selectedColumnIndex])
                : [],
            borderColor: "rgb(75, 192, 192)",
            tension: 0.4,
            fill: false,
          },
        ],
      },
    },
    confidence: {
      confidence: {
        labels: columnHeaders || ["No Data"],
        datasets: [
          {
            label: "Confidence Data",
            data:
              selectedColumnIndex >= 0
                ? data.map((item) => item[selectedColumnIndex])
                : [],
            borderColor: "rgb(75, 192, 192)",
            backgroundColor: "rgba(75, 192, 192, 0.5)",
            type: "line",
          },
        ],
      },
    },
    sampling: {
      randomSampling: {
        labels: data.map((_, index) => `Sample ${index + 1}`),
        datasets: [
          {
            label: "Random Samples",
            data:
              selectedColumnIndex >= 0
                ? data.map((item, index) => ({
                    x: index + 1,
                    y: item[selectedColumnIndex],
                  }))
                : [],
            backgroundColor: "rgb(75, 192, 192)",
          },
        ],
      },
    },
  };

  const renderChart = (type) => {
    const charts = {
      hypothesis: (
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-bold mb-4">Z-Test Distribution</h2>
          <div style={{ height: "300px" }}>
            <Line data={chartData.hypothesis.zTest} options={options} />
          </div>
        </div>
      ),
      confidence: (
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-bold mb-4">Confidence Intervals</h2>
          <div style={{ height: "300px" }}>
            <Line data={chartData.confidence.confidence} options={options} />
          </div>
        </div>
      ),
      sampling: (
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-bold mb-4">Random Sampling Distribution</h2>
          <div style={{ height: "300px" }}>
            <Scatter data={chartData.sampling.randomSampling} options={options} />
          </div>
        </div>
      ),
    };
    return charts[type] || null;
  };

  return (
    <div className="max-w-4xl mx-auto p-6 space-y-6">
      <div className="text-center space-y-4">
        <h1 className="text-2xl font-bold">Inferential Statistics Visualization</h1>
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
          className="px-4 py-2 border rounded-md"
        >
          <option value="hypothesis">Hypothesis Testing</option>
          <option value="confidence">Confidence Intervals</option>
          <option value="sampling">Sampling Methods</option>
        </select>
        <select
          value={selectedColumn}
          onChange={(e) => setSelectedColumn(e.target.value)}
          className="px-4 py-2 border rounded-md"
        >
          {columnHeaders?.map((header, index) => (
            <option key={index} value={header}>
              {header}
            </option>
          ))}
        </select>
      </div>
      {renderChart(selectedCategory)}
    </div>
  );
};

export default InferentialStatisticsVisualiser;
