import React, { useState, useEffect, useCallback } from "react";
import * as ss from "simple-statistics";

const CoreStatistics = ({ data = [], columnHeaders = [] }) => {
  const [selectedColumn, setSelectedColumn] = useState("");
  const [stats, setStats] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const calculateStatistics = useCallback((column) => {
    // Check if data and columnHeaders are properly passed and valid
    if (!Array.isArray(data) || !Array.isArray(columnHeaders) || data.length === 0 || columnHeaders.length === 0) {
      setError("Invalid or empty data/columnHeaders.");
      return {};
    }

    const colIndex = columnHeaders.indexOf(column);
    if (colIndex === -1) {
      setError(`Column "${column}" not found in columnHeaders.`);
      return {};
    }

    // Extract column data, safely handling row structure
    const columnData = data
      .map((row) => (Array.isArray(row) ? parseFloat(row[colIndex]) : NaN))
      .filter((val) => !isNaN(val));

    if (columnData.length === 0) {
      setError(`No valid numeric data found for column "${column}".`);
      return {};
    }

    const min = Math.min(...columnData).toFixed(2);
    const max = Math.max(...columnData).toFixed(2);
    const range = (max - min).toFixed(2);
    const sum = ss.sum(columnData).toFixed(2);
    const count = columnData.length;
    const quartiles =
      columnData.length >= 3
        ? ss.quantileSorted(columnData.sort((a, b) => a - b), [0.25, 0.5, 0.75])
        : [];
    const iqr =
      quartiles.length === 3
        ? (quartiles[2] - quartiles[0]).toFixed(2)
        : "N/A";
    const skewness =
      columnData.length >= 3 ? ss.sampleSkewness(columnData).toFixed(2) : "N/A";
    const kurtosis =
      columnData.length >= 4 ? ss.sampleKurtosis(columnData).toFixed(2) : "N/A";

    return {
      mean: ss.mean(columnData).toFixed(2),
      median: ss.median(columnData).toFixed(2),
      mode: ss.mode(columnData),
      variance: ss.variance(columnData).toFixed(2),
      stdDev: ss.standardDeviation(columnData).toFixed(2),
      range,
      min,
      max,
      sum,
      count,
      iqr,
      quartiles,
      deciles:
        columnData.length >= 10
          ? Array.from({ length: 10 }, (_, i) =>
              ss.quantileSorted(columnData, (i + 1) / 10).toFixed(2)
            )
          : "N/A",
      skewness,
      kurtosis,
    };
  }, [data, columnHeaders]);

  useEffect(() => {
    if (selectedColumn && Array.isArray(data) && Array.isArray(columnHeaders)) {
      setLoading(true);
      setError(null);
      const columnStats = calculateStatistics(selectedColumn);
      setStats(columnStats);
      setLoading(false);
    } else {
      setError("Invalid data or columnHeaders.");
    }
  }, [selectedColumn, data, columnHeaders, calculateStatistics]);

  const handleColumnChange = (e) => {
    setSelectedColumn(e.target.value);
  };

  return (
    <div>
      <select onChange={handleColumnChange} value={selectedColumn}>
        <option value="">Select a column</option>
        {columnHeaders.map((header) => (
          <option key={header} value={header}>
            {header}
          </option>
        ))}
      </select>

      {loading && <p>Loading...</p>}

      {error && <p style={{ color: "red" }}>{error}</p>}

      {selectedColumn && !loading && !error && (
        <div>
          <h3>Core Statistics</h3>
          <p><strong>Mean:</strong> {stats.mean}</p>
          <p><strong>Median:</strong> {stats.median}</p>
          <p><strong>Mode:</strong> {stats.mode}</p>
          <p><strong>Variance:</strong> {stats.variance}</p>
          <p><strong>Standard Deviation:</strong> {stats.stdDev}</p>
          <p><strong>Range:</strong> {stats.range}</p>
          <p><strong>Minimum:</strong> {stats.min}</p>
          <p><strong>Maximum:</strong> {stats.max}</p>
          <p><strong>Sum:</strong> {stats.sum}</p>
          <p><strong>Count:</strong> {stats.count}</p>

          <h3>Advanced Statistics</h3>
          <p><strong>Interquartile Range (IQR):</strong> {stats.iqr}</p>
          <p><strong>Quartiles:</strong> Q1: {stats.quartiles?.[0] || "N/A"}, Q2 (Median): {stats.quartiles?.[1] || "N/A"}, Q3: {stats.quartiles?.[2] || "N/A"}</p>
          <p><strong>Deciles:</strong> {Array.isArray(stats.deciles) ? stats.deciles.join(", ") : stats.deciles}</p>
          <p><strong>Skewness:</strong> {stats.skewness}</p>
          <p><strong>Kurtosis:</strong> {stats.kurtosis}</p>
        </div>
      )}
    </div>
  );
};

export default CoreStatistics;
