import React, { useState } from "react";
import * as ss from "simple-statistics";
import { Line } from "react-chartjs-2"; // Import Chart.js Line chart
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from "chart.js";
import { saveAs } from 'file-saver'; // Import FileSaver for downloading images
import "./DistributionAnalysis.css"; // Import the CSS file

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const DistributionAnalysis = ({ data, columnHeaders }) => {
  const [selectedColumn, setSelectedColumn] = useState("");
  const [selectedDistributionType, setSelectedDistributionType] = useState("");
  const [selectedDistribution, setSelectedDistribution] = useState("");
  const [statistics, setStatistics] = useState({});
  const [chartData, setChartData] = useState(null);
  const [chartRef, setChartRef] = useState(null);

  const continuousDistributions = ["Normal", "Exponential", "Uniform"];
  const discreteDistributions = ["Binomial", "Poisson", "Geometric"];

  const handleColumnChange = (event) => {
    const column = event.target.value;
    setSelectedColumn(column);
    setStatistics({});
    setChartData(null);
    setSelectedDistributionType("");
    setSelectedDistribution("");
    
    if (column) {
      const colIndex = columnHeaders.indexOf(column);
      const columnData = data
        .map((row) => parseFloat(row[colIndex]))
        .filter((val) => !isNaN(val));

      if (columnData.length === 0) return;

      const meanVal = ss.mean(columnData);
      const stdDevVal = ss.standardDeviation(columnData);
      const medianVal = ss.median(columnData);
      const varianceVal = ss.variance(columnData);

      setStatistics({
        mean: meanVal.toFixed(2),
        median: medianVal.toFixed(2),
        variance: varianceVal.toFixed(2),
        stdDev: stdDevVal.toFixed(2),
      });
    }
  };

  const handleDistributionTypeChange = (event) => {
    setSelectedDistributionType(event.target.value);
    setSelectedDistribution("");
    setChartData(null);
  };

  const handleDistributionChange = (event) => {
    const distribution = event.target.value;
    setSelectedDistribution(distribution);

    if (distribution && selectedColumn) {
      const colIndex = columnHeaders.indexOf(selectedColumn);
      const columnData = data
        .map((row) => parseFloat(row[colIndex]))
        .filter((val) => !isNaN(val));

      if (columnData.length === 0) return;

      // Generate chart data for the selected distribution
      const labels = Array.from({ length: columnData.length }, (_, index) => index + 1);
      let distributionData = [];

      switch (distribution) {
        case "Normal":
          distributionData = columnData.map((val) => ss.zScore(val, ss.mean(columnData), ss.standardDeviation(columnData)));
          break;
        case "Exponential":
          distributionData = columnData.map((val) => Math.exp(-val / ss.mean(columnData)));
          break;
        case "Uniform":
          distributionData = columnData.map((val) => 1 / (Math.max(...columnData) - Math.min(...columnData)));
          break;
        case "Binomial":
          distributionData = columnData.map((val) => ss.binomialDistribution(val, Math.max(...columnData), 0.5));
          break;
        case "Poisson":
          distributionData = columnData.map((val) => ss.poissonDistribution(val, ss.mean(columnData)));
          break;
        case "Geometric":
          distributionData = columnData.map((val) => ss.geometricMean([val]));
          break;
        default:
          break;
      }

      setChartData({
        labels,
        datasets: [
          {
            label: `${selectedDistribution} Distribution`,
            data: distributionData,
            fill: false,
            borderColor: "#3498db", // Change line color to blue for better visibility
            tension: 0.3, // Increase curve tension for a smoother line
          },
        ],
      });
    }
  };

  const handleDownloadChart = () => {
    if (chartRef) {
      const canvas = chartRef.chartInstance.canvas;
      canvas.toBlob((blob) => {
        saveAs(blob, `${selectedDistribution}_${selectedColumn}_Chart.png`);
      });
    }
  };

  return (
    <div className="distribution-analysis-container">
      <h3 className="header">Distribution Analysis</h3>
      <div className="form-container">
        <div className="select-container">
          <div>
            <label className="label">
              Select Column:
              <select 
                onChange={handleColumnChange} 
                className="select-input"
              >
                <option value="">Select a column</option>
                {columnHeaders.map((header) => (
                  <option key={header} value={header}>
                    {header}
                  </option>
                ))}
              </select>
            </label>
          </div>

          {selectedColumn && (
            <div>
              <label className="label">
                Select Distribution Type:
                <select 
                  onChange={handleDistributionTypeChange} 
                  className="select-input"
                >
                  <option value="">Select Type</option>
                  <option value="Continuous">Continuous</option>
                  <option value="Discrete">Discrete</option>
                </select>
              </label>
            </div>
          )}
        </div>

        {selectedDistributionType && (
          <div className="distribution-options">
            <label className="label">
              Select {selectedDistributionType} Distribution:
              <select 
                onChange={handleDistributionChange} 
                className="select-input"
              >
                <option value="">Select Distribution</option>
                {(selectedDistributionType === "Continuous" ? continuousDistributions : discreteDistributions).map((dist) => (
                  <option key={dist} value={dist}>
                    {dist}
                  </option>
                ))}
              </select>
            </label>
          </div>
        )}
      </div>

      {selectedColumn && statistics.mean && (
        <div className="statistics">
          <h4>Statistics for {selectedColumn}</h4>
          <ul>
            <li><strong>Mean:</strong> {statistics.mean}</li>
            <li><strong>Median:</strong> {statistics.median}</li>
            <li><strong>Variance:</strong> {statistics.variance}</li>
            <li><strong>Standard Deviation:</strong> {statistics.stdDev}</li>
          </ul>
        </div>
      )}

      {chartData && (
        <div className="chart-container">
          <Line
            data={chartData}
            ref={(ref) => setChartRef(ref)} // Store chart reference for downloading
            options={{
              responsive: true,
              maintainAspectRatio: false,
              plugins: { title: { display: true, text: `${selectedDistribution} Distribution for ${selectedColumn}` } },
              scales: {
                x: {
                  title: {
                    display: true,
                    text: "Index",
                    color: "#2C3E50",
                  },
                },
                y: {
                  title: {
                    display: true,
                    text: "Value",
                    color: "#2C3E50",
                  },
                },
              },
            }}
          />
          <button onClick={handleDownloadChart} className="download-btn">Download Chart</button>
        </div>
      )}
    </div>
  );
};

export default DistributionAnalysis;
