import React, { useState } from "react";
import * as ss from "simple-statistics";
import { Line } from "react-chartjs-2";
import "../styles/DistributionAnalysis.css";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import jStat from "jstat";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const DistributionAnalysis = ({ data, columnHeaders }) => {
  const [selectedColumn, setSelectedColumn] = useState("");
  const [selectedDistributionType, setSelectedDistributionType] = useState("");
  const [selectedDistribution, setSelectedDistribution] = useState("");
  const [chartData, setChartData] = useState(null);
  const [accuracy, setAccuracy] = useState(null);

  const continuousDistributions = ["Normal", "Exponential", "Uniform", "Gamma", "Log-Normal", "Beta"];
  const discreteDistributions = ["Binomial", "Poisson", "Geometric", "Bernoulli", "Negative Binomial", "Hypergeometric", "Multinomial"];

  const handleColumnChange = (event) => {
    const column = event.target.value;
    setSelectedColumn(column);
    setChartData(null);
    setSelectedDistributionType("");
    setSelectedDistribution("");
    setAccuracy(null);
  };

  const handleDistributionTypeChange = (event) => {
    setSelectedDistributionType(event.target.value);
    setSelectedDistribution("");
    setChartData(null);
    setAccuracy(null);
  };

  const handleDistributionChange = (event) => {
    const distribution = event.target.value;
    setSelectedDistribution(distribution);
    setAccuracy(null);

    if (distribution && selectedColumn) {
      const colIndex = columnHeaders.indexOf(selectedColumn);
      const columnData = data
        .map((row) => parseFloat(row[colIndex]))
        .filter((val) => !isNaN(val));

      if (columnData.length === 0) return;

      const labels = Array.from({ length: columnData.length }, (_, index) => index + 1);
      let distributionData = [];
      let calculatedAccuracy = 0;

      switch (distribution) {
        case "Normal":
          const mean = ss.mean(columnData);
          const stddev = ss.standardDeviation(columnData);
          distributionData = columnData.map((val) => (1 / (stddev * Math.sqrt(2 * Math.PI))) * Math.exp(-Math.pow(val - mean, 2) / (2 * Math.pow(stddev, 2))));
          calculatedAccuracy = calculateAccuracy(columnData, distributionData);
          break;
        case "Exponential":
          const lambda = 1 / ss.mean(columnData);
          distributionData = columnData.map((val) => lambda * Math.exp(-lambda * val));
          calculatedAccuracy = calculateAccuracy(columnData, distributionData);
          break;
        case "Uniform":
          const min = Math.min(...columnData);
          const max = Math.max(...columnData);
          distributionData = columnData.map((val) => (val >= min && val <= max ? 1 / (max - min) : 0));
          calculatedAccuracy = calculateAccuracy(columnData, distributionData);
          break;
        case "Gamma":
          const alphaGamma = Math.pow(ss.mean(columnData), 2) / Math.pow(ss.standardDeviation(columnData), 2);
          const betaGamma = ss.mean(columnData) / alphaGamma;
          distributionData = columnData.map((val) => jStat.gamma.pdf(val, alphaGamma, betaGamma));
          calculatedAccuracy = calculateAccuracy(columnData, distributionData);
          break;
        case "Log-Normal":
          const logMean = Math.log(ss.mean(columnData));
          const logStddev = Math.log(ss.standardDeviation(columnData));
          distributionData = columnData.map((val) => (1 / (val * logStddev * Math.sqrt(2 * Math.PI))) * Math.exp(-Math.pow(Math.log(val) - logMean, 2) / (2 * Math.pow(logStddev, 2))));
          calculatedAccuracy = calculateAccuracy(columnData, distributionData);
          break;
        case "Beta":
          const meanBeta = ss.mean(columnData);
          const varianceBeta = Math.pow(ss.standardDeviation(columnData), 2);
          const alphaBeta = meanBeta * ((meanBeta * (1 - meanBeta)) / varianceBeta - 1);
          const betaBeta = (1 - meanBeta) * ((meanBeta * (1 - meanBeta)) / varianceBeta - 1);
          distributionData = columnData.map((val) => jStat.beta.pdf(val, alphaBeta, betaBeta));
          calculatedAccuracy = calculateAccuracy(columnData, distributionData);
          break;
        default:
          break;
      }

      setChartData({
        labels,
        datasets: [
          {
            label: `${selectedDistribution} Distribution`,
            data: distributionData,
            fill: false,
            borderColor: "rgba(75,192,192,1)",
            tension: 0.1,
          },
        ],
      });
      setAccuracy(calculatedAccuracy);
    }
  };

  const calculateAccuracy = (originalData, fittedData) => {
    const residuals = originalData.map((val, idx) => Math.abs(val - fittedData[idx]));
    const meanResidual = ss.mean(residuals);
    const accuracy = 100 - (meanResidual / ss.mean(originalData)) * 100;
    return Math.max(0, Math.min(100, accuracy)); // Ensure accuracy is between 0 and 100
  };

  return (
    <div className="distribution-analysis">
      <h3>Distribution Analysis</h3>
      <div className="controls">
        <div className="select-group">
          <label>Select Column:</label>
          <select onChange={handleColumnChange}>
            <option value="">Select a column</option>
            {columnHeaders.map((header) => (
              <option key={header} value={header}>
                {header}
              </option>
            ))}
          </select>
        </div>

        {selectedColumn && (
          <>
            <div className="select-group">
              <label>Select Distribution Type:</label>
              <select onChange={handleDistributionTypeChange}>
                <option value="">Select Type</option>
                <option value="Continuous">Continuous</option>
                <option value="Discrete">Discrete</option>
              </select>
            </div>

            {selectedDistributionType === "Continuous" && (
              <div className="select-group">
                <label>Select Continuous Distribution:</label>
                <select onChange={handleDistributionChange}>
                  <option value="">Select Distribution</option>
                  {continuousDistributions.map((dist) => (
                    <option key={dist} value={dist}>
                      {dist}
                    </option>
                  ))}
                </select>
              </div>
            )}
          </>
        )}
      </div>

      {chartData && (
        <div className="chart-container">
          <Line
            data={chartData}
            options={{
              responsive: true,
              plugins: { title: { display: true, text: `${selectedDistribution} Distribution for ${selectedColumn}` } },
            }}
          />
          <p>Accuracy: {accuracy.toFixed(2)}%</p>
        </div>
      )}
    </div>
  );
};

export default DistributionAnalysis;
