import React, { useState } from 'react';
import { Line, Bar, Scatter } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const InferentialStatisticsViz = ({ data, columnHeaders }) => {
  const [selectedCategory, setSelectedCategory] = useState('hypothesis');

  // Dynamically generate chart data
  const generateChartData = (category) => {
    const labels = columnHeaders;
    const dataset = {
      label: `${category} Data`,
      data: data,
      backgroundColor: 'rgba(75, 192, 192, 0.5)',
      borderColor: 'rgb(75, 192, 192)',
      tension: 0.4,
      fill: false,
    };

    return {
      labels,
      datasets: [dataset],
    };
  };

  // Options for all charts
  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
    },
  };

  // Render functions for each category
  const renderHypothesisTests = () => (
    <div className="bg-white p-6 rounded-lg shadow">
      <h2 className="text-xl font-bold mb-4">Hypothesis Testing</h2>
      <div style={{ height: '300px' }}>
        <Line data={generateChartData('Hypothesis Testing')} options={options} />
      </div>
    </div>
  );

  const renderConfidenceIntervals = () => (
    <div className="bg-white p-6 rounded-lg shadow">
      <h2 className="text-xl font-bold mb-4">Confidence Intervals</h2>
      <div style={{ height: '300px' }}>
        <Line data={generateChartData('Confidence Intervals')} options={options} />
      </div>
    </div>
  );

  const renderSampling = () => (
    <div className="bg-white p-6 rounded-lg shadow">
      <h2 className="text-xl font-bold mb-4">Sampling Methods</h2>
      <div style={{ height: '300px' }}>
        <Scatter data={generateChartData('Sampling Methods')} options={options} />
      </div>
    </div>
  );

  return (
    <div className="max-w-4xl mx-auto p-6 space-y-6">
      <div className="text-center space-y-4">
        <h1 className="text-2xl font-bold">Inferential Statistics Visualization</h1>
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
          className="px-4 py-2 border rounded-md"
        >
          <option value="hypothesis">Hypothesis Testing</option>
          <option value="confidence">Confidence Intervals</option>
          <option value="sampling">Sampling Methods</option>
        </select>
      </div>

      {selectedCategory === 'hypothesis' && renderHypothesisTests()}
      {selectedCategory === 'confidence' && renderConfidenceIntervals()}
      {selectedCategory === 'sampling' && renderSampling()}
    </div>
  );
};

export default InferentialStatisticsViz;
