import React, { useState } from "react";
import * as ss from "simple-statistics";
import { Line, Bar } from "react-chartjs-2";
import "../styles/DistributionAnalysis.css";
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, BarElement, Title, Tooltip, Legend } from "chart.js";
import jStat from "jstat";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, Title, Tooltip, Legend);

const DistributionAnalysis = ({ data, columnHeaders }) => {
  const [selectedColumn, setSelectedColumn] = useState("");
  const [selectedDistributionType, setSelectedDistributionType] = useState("");
  const [selectedDistribution, setSelectedDistribution] = useState("");
  const [chartData, setChartData] = useState(null);
  const [accuracy, setAccuracy] = useState(null);
  const [params, setParams] = useState({});

  const continuousDistributions = [
    "Normal", "Exponential", "Uniform", "Gamma", "Log-Normal", "Beta", "Weibull", "Chi-Square", "Cauchy", "T-Distribution"
  ];

  const discreteDistributions = [
    "Binomial", "Poisson", "Geometric", "Bernoulli", "Negative Binomial", "Hypergeometric"
  ];

  const handleColumnChange = (event) => {
    const column = event.target.value;
    setSelectedColumn(column);
    setChartData(null);
    setSelectedDistributionType("");
    setSelectedDistribution("");
    setAccuracy(null);
  };

  const handleDistributionTypeChange = (event) => {
    setSelectedDistributionType(event.target.value);
    setSelectedDistribution("");
    setChartData(null);
    setAccuracy(null);
  };

  const handleDistributionChange = (event) => {
    const distribution = event.target.value;
    setSelectedDistribution(distribution);

    if (distribution && selectedColumn) {
      const colIndex = columnHeaders.indexOf(selectedColumn);
      const columnData = data.map((row) => parseFloat(row[colIndex])).filter((val) => !isNaN(val));
      if (columnData.length === 0) return;

      const labels = Array.from({ length: columnData.length }, (_, index) => index + 1);
      let distributionData = [];
      let calculatedAccuracy = 0;
      let chartType = "line";

      const dynamicParams = {};

      switch (distribution) {
        case "Normal":
          const mean = ss.mean(columnData);
          const stddev = ss.standardDeviation(columnData);
          dynamicParams.mean = mean;
          dynamicParams.stddev = stddev;
          distributionData = columnData.map((val) => jStat.normal.pdf(val, mean, stddev));
          calculatedAccuracy = "Goodness of fit: " + (1 - (stddev / mean)).toFixed(4);
          chartType = "line"; // bell curve
          break;

        case "Exponential":
          const lambda = 1 / ss.mean(columnData);
          dynamicParams.lambda = lambda;
          distributionData = columnData.map((val) => jStat.exponential.pdf(val, lambda));
          calculatedAccuracy = "Goodness of fit: " + lambda.toFixed(4);
          chartType = "line"; // exponential decay curve
          break;

        case "Uniform":
          const min = Math.min(...columnData);
          const max = Math.max(...columnData);
          dynamicParams.min = min;
          dynamicParams.max = max;
          distributionData = columnData.map(() => 1 / (max - min));
          calculatedAccuracy = "Goodness of fit: " + (max - min).toFixed(4);
          chartType = "line"; // uniform distribution curve
          break;

        case "Gamma":
          const shape = 2; // dynamic input for shape
          const scale = ss.mean(columnData) / shape;
          dynamicParams.shape = shape;
          dynamicParams.scale = scale;
          distributionData = columnData.map((val) => jStat.gamma.pdf(val, shape, scale));
          calculatedAccuracy = "Goodness of fit: " + (shape * scale).toFixed(4);
          chartType = "line"; // gamma distribution curve
          break;

        case "Log-Normal":
          const logMean = Math.log(ss.mean(columnData));
          const logStdDev = Math.log(ss.standardDeviation(columnData));
          dynamicParams.logMean = logMean;
          dynamicParams.logStdDev = logStdDev;
          distributionData = columnData.map((val) => jStat.lognormal.pdf(val, logMean, logStdDev));
          calculatedAccuracy = "Goodness of fit: " + (1 / logStdDev).toFixed(4);
          chartType = "line"; // log-normal distribution curve
          break;

        case "Beta":
          const alpha = 2; // dynamic input for alpha
          const beta = 5; // dynamic input for beta
          dynamicParams.alpha = alpha;
          dynamicParams.beta = beta;
          distributionData = columnData.map((val) => jStat.beta.pdf(val, alpha, beta));
          calculatedAccuracy = "Goodness of fit: " + (alpha / (alpha + beta)).toFixed(4);
          chartType = "line"; // beta distribution curve
          break;

        case "Weibull":
          const weibullScale = ss.mean(columnData);
          const weibullShape = 1.5; // dynamic input for shape
          dynamicParams.weibullShape = weibullShape;
          dynamicParams.weibullScale = weibullScale;
          distributionData = columnData.map((val) => jStat.weibull.pdf(val, weibullShape, weibullScale));
          calculatedAccuracy = "Goodness of fit: " + (weibullScale / weibullShape).toFixed(4);
          chartType = "line"; // weibull distribution curve
          break;

        case "Chi-Square":
          const df = columnData.length - 1; // degrees of freedom
          dynamicParams.df = df;
          distributionData = columnData.map((val) => jStat.chisquare.pdf(val, df));
          calculatedAccuracy = "Goodness of fit: " + (1 / df).toFixed(4);
          chartType = "line"; // chi-square distribution curve
          break;

        case "Cauchy":
          const location = ss.mean(columnData);
          const scaleParam = ss.standardDeviation(columnData);
          dynamicParams.location = location;
          dynamicParams.scaleParam = scaleParam;
          distributionData = columnData.map((val) => jStat.cauchy.pdf(val, location, scaleParam));
          calculatedAccuracy = "Goodness of fit: " + (1 / scaleParam).toFixed(4);
          chartType = "line"; // cauchy distribution curve
          break;

        case "T-Distribution":
          const dof = columnData.length - 1; // degrees of freedom
          dynamicParams.dof = dof;
          distributionData = columnData.map((val) => jStat.studentt.pdf(val, dof));
          calculatedAccuracy = "Goodness of fit: " + (1 / dof).toFixed(4);
          chartType = "line"; // t-distribution curve
          break;

        case "Binomial":
          const trials = 10; // dynamic input for trials
          const probability = 0.5; // dynamic input for probability
          dynamicParams.trials = trials;
          dynamicParams.probability = probability;
          distributionData = columnData.map((val) => ss.binomialDistribution(trials, probability)[val] || 0);
          calculatedAccuracy = "Goodness of fit: " + (trials * probability).toFixed(4);
          chartType = "bar"; // binomial histogram
          break;

        case "Poisson":
          const poissonLambda = ss.mean(columnData);
          dynamicParams.poissonLambda = poissonLambda;
          distributionData = columnData.map((val) => ss.poissonDistribution(poissonLambda)[val] || 0);
          calculatedAccuracy = "Goodness of fit: " + poissonLambda.toFixed(4);
          chartType = "bar"; // Poisson histogram
          break;

        case "Geometric":
          const geomP = 1 / ss.mean(columnData); // dynamic input for probability
          dynamicParams.geomP = geomP;
          distributionData = columnData.map((val) => geomP * Math.pow(1 - geomP, val - 1));
          calculatedAccuracy = "Goodness of fit: " + geomP.toFixed(4);
          chartType = "bar"; // geometric distribution histogram
          break;

        case "Bernoulli":
          const bernP = ss.mean(columnData); // dynamic input for probability
          dynamicParams.bernP = bernP;
          distributionData = columnData.map((val) => (val === 0 ? 1 - bernP : bernP));
          calculatedAccuracy = "Goodness of fit: " + bernP.toFixed(4);
          chartType = "bar"; // bernoulli distribution histogram
          break;

        case "Negative Binomial":
          const negR = 5; // dynamic input for number of successes
          const negP = 0.5; // dynamic input for probability
          dynamicParams.negR = negR;
          dynamicParams.negP = negP;
          distributionData = columnData.map((val) => jStat.negbinom.pdf(val, negR, negP));
          calculatedAccuracy = "Goodness of fit: " + (negR * negP).toFixed(4);
          chartType = "bar"; // negative binomial histogram
          break;

        case "Hypergeometric":
          const N = 50; // dynamic input for population size
          const K = 20; // dynamic input for number of success states
          const n = 10; // dynamic input for sample size
          dynamicParams.N = N;
          dynamicParams.K = K;
          dynamicParams.n = n;
          distributionData = columnData.map((val) => jStat.hypergeometric.pdf(val, N, K, n));
          calculatedAccuracy = "Goodness of fit: " + (K / N).toFixed(4);
          chartType = "bar"; // hypergeometric distribution histogram
          break;

        default:
          break;
      }

      setParams(dynamicParams);
      setAccuracy(calculatedAccuracy);
      setChartData({
        labels: labels,
        datasets: [
          {
            label: `${distribution} Distribution`,
            data: distributionData,
            fill: false,
            borderColor: "rgba(75,192,192,1)",
            tension: 0.1,
          },
        ],
      });
    }
  };

  const renderChart = () => {
    const options = {
      responsive: true,
      plugins: {
        title: { display: true, text: `${selectedDistribution} Distribution for ${selectedColumn}` },
      },
    };

    return chartData ? (
      <div className="chart-container">
        {selectedDistribution === "Normal" ||
        selectedDistribution === "Exponential" ||
        selectedDistribution === "Gamma" ||
        selectedDistribution === "Log-Normal" ||
        selectedDistribution === "Beta" ||
        selectedDistribution === "Weibull" ||
        selectedDistribution === "Chi-Square" ||
        selectedDistribution === "Cauchy" ||
        selectedDistribution === "T-Distribution" ? (
          <Line data={chartData} options={options} />
        ) : (
          <Bar data={chartData} options={options} />
        )}
      </div>
    ) : null;
  };

  return (
    <div className="distribution-analysis">
      <h3>Distribution Analysis</h3>
      <div className="controls">
        <label>Select Column:</label>
        <select onChange={handleColumnChange}>
          <option value="">Select a column</option>
          {columnHeaders.map((header) => (
            <option key={header} value={header}>
              {header}
            </option>
          ))}
        </select>

        {selectedColumn && (
          <>
            <label>Select Distribution Type:</label>
            <select onChange={handleDistributionTypeChange}>
              <option value="">Select Type</option>
              <option value="Continuous">Continuous</option>
              <option value="Discrete">Discrete</option>
            </select>

            {selectedDistributionType === "Continuous" && (
              <select onChange={handleDistributionChange}>
                <option value="">Select Distribution</option>
                {continuousDistributions.map((dist) => (
                  <option key={dist} value={dist}>
                    {dist}
                  </option>
                ))}
              </select>
            )}

            {selectedDistributionType === "Discrete" && (
              <select onChange={handleDistributionChange}>
                <option value="">Select Distribution</option>
                {discreteDistributions.map((dist) => (
                  <option key={dist} value={dist}>
                    {dist}
                  </option>
                ))}
              </select>
            )}
          </>
        )}
      </div>

      {renderChart()}

      {accuracy && (
        <div className="accuracy-container">
          <h4>Accuracy: {accuracy}</h4>
        </div>
      )}
    </div>
  );
};

export default DistributionAnalysis;
