import React, { useState, useEffect } from "react";
import { Bar, Pie, Line } from "react-chartjs-2";

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement
);

const InferentialStatisticsVisualiser = ({ data, columnHeaders }) => {
  const [selectedTopic, setSelectedTopic] = useState(columnHeaders[0]);

  // Prepare dynamic chart data based on props
  const prepareChartData = () => {
    return columnHeaders.reduce((acc, header, index) => {
      const values = data.map((row) => row[index]);
      const chartType = index % 2 === 0 ? "Bar" : "Pie";

      acc[header] = {
        type: chartType,
        data: {
          labels: data.map((row) => row[0]),
          datasets: [
            {
              label: header,
              data: values,
              backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56", "#4BC0C0"],
            },
          ],
        },
      };
      return acc;
    }, {});
  };

  const chartData = prepareChartData();

  // Render Chart Based on Selected Topic
  const renderChart = () => {
    const chart = chartData[selectedTopic];
    if (chart.type === "Bar") {
      return <Bar data={chart.data} options={{ responsive: true, plugins: { legend: { position: "top" } } }} />;
    }
    if (chart.type === "Pie") {
      return <Pie data={chart.data} options={{ responsive: true, plugins: { legend: { position: "right" } } }} />;
    }
    if (chart.type === "Line") {
      return (
        <Line
          data={chart.data}
          options={{
            responsive: true,
            plugins: { legend: { position: "top" } },
            animation: { duration: 3000, easing: "easeOutBounce" },
          }}
        />
      );
    }
    return null;
  };

  // Test Visualization Accuracy
  useEffect(() => {
    const testVisualizationAccuracy = () => {
      columnHeaders.forEach((header) => {
        const chart = chartData[header];
        if (chart) {
          console.log(`✅ Test passed for topic: ${header}`);
        } else {
          console.error(`❌ Test failed for topic: ${header}`);
        }
      });
    };

    testVisualizationAccuracy();
  }, [chartData, columnHeaders]);

  // Render Component
  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Inferential Statistics Visualization</h1>
      <div style={styles.dropdownContainer}>
        <label htmlFor="topic-select" style={styles.label}>Select Topic:</label>
        <select
          id="topic-select"
          style={styles.select}
          value={selectedTopic}
          onChange={(e) => setSelectedTopic(e.target.value)}
        >
          {columnHeaders.map((topic) => (
            <option key={topic} value={topic}>
              {topic}
            </option>
          ))}
        </select>
      </div>
      <div style={styles.chartContainer}>{renderChart()}</div>
    </div>
  );
};

// Styles
const styles = {
  container: {
    fontFamily: "Arial, sans-serif",
    maxWidth: "800px",
    margin: "20px auto",
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "12px",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.15)",
    backgroundColor: "#f9f9f9",
  },
  title: {
    textAlign: "center",
    color: "#444",
    marginBottom: "20px",
  },
  dropdownContainer: {
    textAlign: "center",
    marginBottom: "20px",
  },
  label: {
    fontSize: "16px",
    marginRight: "10px",
    color: "#333",
  },
  select: {
    padding: "10px",
    fontSize: "16px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    outline: "none",
    cursor: "pointer",
  },
  chartContainer: {
    marginTop: "20px",
    padding: "20px",
    backgroundColor: "#fff",
    borderRadius: "8px",
    boxShadow: "0 2px 8px rgba(0, 0, 0, 0.1)",
  },
};

export default InferentialStatisticsVisualiser;
